tables.table_schema:
CREATE TABLE GreenhouseEnvironment (
    id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE greenhouse_environment_data (
    id INT PRIMARY KEY,
    time TIMESTAMP,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE plant_growth_data (
    id INT PRIMARY KEY,
    plant_id INT,
    growth_stage VARCHAR,
    growth_rate FLOAT
);

CREATE TABLE greenhouse_environment(
    id INT PRIMARY KEY,
    date TIMESTAMP,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE Greenhouse (
    ID INT PRIMARY KEY,
    Temperature FLOAT,
    Humidity FLOAT,
    Date TIMESTAMP
);

CREATE TABLE Plant (
    PlantID INT PRIMARY KEY,
    PlantName VARCHAR(50),
    PlantType VARCHAR(50),
    PlantVariety VARCHAR(50)
);

CREATE TABLE Sensor (
    SensorID INT PRIMARY KEY,
    SensorType VARCHAR(50),
    SensorLocation VARCHAR(50),
    Status BOOLEAN
);

CREATE TABLE Event (
    EventID INT PRIMARY KEY,
    EventType VARCHAR(50),
    EventDescription TEXT,
    EventTime TIMESTAMP
);
