tables.table_schema:
CREATE TABLE satellite_images (
  image_id INT PRIMARY KEY,
  image_data BYTEA,
  geographic_coordinates VARCHAR(50),
  capture_date DATE
);

CREATE TABLE SatelliteImages (
    image_id SERIAL PRIMARY KEY,
    image_name TEXT NOT NULL,
    coordinates JSONB NOT NULL,
    capture_date DATE NOT NULL
);

CREATE TABLE LandCoverAnalysis (
    analysis_id SERIAL PRIMARY KEY,
    image_id INTEGER REFERENCES SatelliteImages(image_id),
    land_cover_type TEXT NOT NULL,
    analysis_result JSONB NOT NULL
);

CREATE TABLE land_cover_analysis (
    id UUID PRIMARY KEY,
    analysis_date DATE NOT NULL,
    analysis_result TEXT,
    satellite_image_id UUID REFERENCES satellite_images(id)
);

CREATE TABLE geographic_coordinates (
    id UUID PRIMARY KEY,
    latitude DECIMAL NOT NULL,
    longitude DECIMAL NOT NULL,
    location_name TEXT
);

CREATE TABLE ImageAnalysis (
    analysis_id UUID PRIMARY KEY,
    image_id UUID REFERENCES SatelliteImages(image_id),
    analysis_date TIMESTAMP,
    analysis_result JSON
);

CREATE TABLE Coordinates (
    coordinate_id UUID PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    location_description VARCHAR
);

CREATE TABLE LandCoverChange (
    change_id UUID PRIMARY KEY,
    coordinate_id UUID REFERENCES Coordinates(coordinate_id),
    change_date TIMESTAMP,
    land_cover_type VARCHAR
);

CREATE TABLE satellite_images (
    image_id UUID PRIMARY KEY,
    coordinates GEOMETRY NOT NULL,
    image_data BYTEA,
    acquisition_date DATE
);

CREATE TABLE land_cover_classes (
    class_id SERIAL PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL
);

CREATE TABLE image_analysis_results (
    analysis_id SERIAL PRIMARY KEY,
    image_id UUID,
    analysis_date TIMESTAMP,
    land_cover_class_id INT,
    analysis_result TEXT
);

CREATE TABLE geographic_coordinates (
    coordinate_id SERIAL PRIMARY KEY,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE land_cover_changes (
    change_id SERIAL PRIMARY KEY,
    image_id UUID,
    analysis_id INT,
    change_date DATE,
    change_type VARCHAR(50)
);
