tables.table_schema:
CREATE TABLE satellite_images (
   image_id INT PRIMARY KEY,
   image_name VARCHAR(50),
   image_data BYTEA,
   upload_date TIMESTAMP
);

CREATE TABLE disaster_areas (
   area_id INT PRIMARY KEY,
   area_name VARCHAR(50),
   affected_image_id INT REFERENCES satellite_images(image_id),
   area_geometry GEOMETRY
);

CREATE TABLE satellite_images (
    id SERIAL PRIMARY KEY,
    image_url TEXT,
    captured_date DATE,
    image_data BYTEA
);

CREATE TABLE disaster_types (
    id SERIAL PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE
);

CREATE TABLE SatelliteImages (
    image_id UUID PRIMARY KEY,
    image_path STRING NOT NULL,
    image_date DATE NOT NULL,
    image_capture_time TIME NOT NULL
);

CREATE TABLE AffectedAreas (
    area_id UUID PRIMARY KEY,
    image_id UUID REFERENCES SatelliteImages(image_id),
    area_name STRING NOT NULL,
    area_polygon GEOMETRY NOT NULL
);

CREATE TABLE DisasterReports (
    report_id UUID PRIMARY KEY,
    disaster_id UUID REFERENCES NaturalDisasters(disaster_id),
    report_date DATE NOT NULL,
    report_content STRING
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username STRING NOT NULL,
    email STRING UNIQUE NOT NULL,
    created_at TIMESTAMP NOT NULL
);
