tables.table_schema:
CREATE TABLE satellite_images (
    id INT PRIMARY KEY,
    image_url TEXT,
    location TEXT,
    timestamp TIMESTAMPTZ
);

CREATE TABLE mining_activities (
    id INT PRIMARY KEY,
    satellite_image_id INT,
    activity_type TEXT,
    detected_area FLOAT,
    CONSTRAINT fk_satellite_images FOREIGN KEY (satellite_image_id) REFERENCES satellite_images(id)
);

CREATE TABLE remote_areas (
    area_id UUID PRIMARY KEY,
    area_name VARCHAR(100),
    area_location geography
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
