tables.table_schema:
CREATE TABLE satellite_images (
    id SERIAL PRIMARY KEY,
    image_url VARCHAR(255) NOT NULL,
    region_name VARCHAR(100) NOT NULL,
    temperature FLOAT NOT NULL
);

CREATE TABLE heat_maps (
    image_id UUID PRIMARY KEY,
    image_data BYTEA,
    region_id INT,
    temperature FLOAT
);

CREATE TABLE regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50)
);

CREATE TABLE temperature_data (
    data_id SERIAL PRIMARY KEY,
    image_id UUID,
    region_id INT,
    temperature_reading FLOAT
);

CREATE TABLE Temperature_Data (
  satellite_id INT,
  latitude DOUBLE PRECISION,
  longitude DOUBLE PRECISION,
  temperature FLOAT
);

CREATE TABLE Regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(50)
);

CREATE TABLE Satellite_Images (
  satellite_id INT PRIMARY KEY,
  image_url VARCHAR(255),
  capture_date DATE
);

CREATE TABLE Heat_Map (
  heat_map_id INT PRIMARY KEY,
  region_id INT,
  satellite_id INT,
  heat_image_url VARCHAR(255)
);

CREATE TABLE SatelliteImages (
    image_id INT PRIMARY KEY,
    image_data BYTES
);

CREATE TABLE ImageMetadata (
    image_id INT,
    location VARCHAR,
    timestamp TIMESTAMP,
    FOREIGN KEY (image_id) REFERENCES SatelliteImages(image_id)
);

CREATE TABLE TemperatureData (
    temperature_id INT PRIMARY KEY,
    image_id INT,
    temperature FLOAT,
    FOREIGN KEY (image_id) REFERENCES SatelliteImages(image_id)
);

CREATE TABLE Regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR
);
