tables.table_schema:
CREATE TABLE satellite_images (
    image_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    image_data BYTEA
);

CREATE TABLE coastal_erosion (
    erosion_id UUID PRIMARY KEY,
    image_id UUID,
    erosion_area GEOMETRY,
    erosion_extent DECIMAL
);

CREATE TABLE satellite_images (
    image_id UUID PRIMARY KEY,
    image_timestamp TIMESTAMP,
    location VARCHAR,
    erosion_level FLOAT
);

CREATE TABLE satellite_images (
    image_id INT,
    image_timestamp TIMESTAMP,
    image_url TEXT
);

CREATE TABLE erosion_assessment (
    assessment_id INT,
    assessment_date DATE,
    erosion_level INT,
    confidence_score FLOAT
);

CREATE TABLE human_activities (
    activity_id INT,
    activity_type TEXT,
    activity_date DATE
);

CREATE TABLE coastal_areas (
    area_id INT,
    area_name TEXT,
    erosion_impact INT
);
