tables.table_schema:
CREATE TABLE crop_schedule (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    irrigation_time TIME,
    weather_conditions VARCHAR(100)
);

CREATE TABLE Crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR,
    irrigation_schedule VARCHAR,
    weather_forecast VARCHAR
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    password VARCHAR
);

CREATE TABLE Crop (
    crop_id UUID PRIMARY KEY,
    crop_name STRING NOT NULL,
    crop_type STRING NOT NULL,
    planting_date DATE NOT NULL
);

CREATE TABLE WeatherForecast (
    forecast_id UUID PRIMARY KEY,
    forecast_date DATE NOT NULL,
    temperature FLOAT NOT NULL,
    humidity FLOAT NOT NULL
);

CREATE TABLE Farm (
    farm_id UUID PRIMARY KEY,
    farm_name STRING NOT NULL,
    location STRING NOT NULL
);

CREATE TABLE WeatherStation (
    station_id UUID PRIMARY KEY,
    station_name STRING NOT NULL,
    location STRING NOT NULL
);
