tables.table_schema:
CREATE TABLE WeatherData (
    city VARCHAR,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE CropData (
    crop_name VARCHAR,
    planting_date DATE,
    harvest_date DATE,
    quantity INT
);

CREATE TABLE weather_data (
	datetime TIMESTAMP PRIMARY KEY,
	temperature FLOAT,
	humidity FLOAT,
	wind_speed FLOAT
);

CREATE TABLE rainfall_data (
	datetime TIMESTAMP PRIMARY KEY,
	rainfall FLOAT,
	location VARCHAR(50)
);

CREATE TABLE crop_data (
	crop_id SERIAL PRIMARY KEY,
	crop_name VARCHAR(50),
	planting_date DATE,
	harvest_date DATE
);

CREATE TABLE WeatherPatterns (
  pattern_id INT,
  pattern_name VARCHAR,
  description TEXT
);

CREATE TABLE crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    crop_type VARCHAR(50),
    planting_season VARCHAR(50)
);

CREATE TABLE crop_yield (
    yield_id INT PRIMARY KEY,
    crop_id INT,
    date DATE,
    yield_per_acre FLOAT
);

CREATE TABLE farmer (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50),
    location_id INT,
    crop_id INT
);
