tables.table_schema:
CREATE TABLE Pesticides (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  application_rate DECIMAL,
  manufacturer VARCHAR(255)
);

CREATE TABLE Fertilizers (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  application_rate DECIMAL,
  npk_ratio VARCHAR(10)
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE logs (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  event VARCHAR(255),
  detail TEXT
);
