tables.table_schema:
CREATE TABLE crop_yield_data (
  crop_id SERIAL PRIMARY KEY,
  crop_name TEXT,
  yield_amount FLOAT,
  planting_date DATE
);

CREATE TABLE productivity_trends (
  trend_id SERIAL PRIMARY KEY,
  trend_category TEXT,
  yield_increase FLOAT,
  analysis_date DATE
);

CREATE TABLE planting_seasons (
    season_id INT PRIMARY KEY,
    season_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE decision_making (
    decision_id INT PRIMARY KEY,
    analysis_id INT,
    decision_date DATE,
    recommended_actions TEXT
);

CREATE TABLE crop_yield (
    id INT PRIMARY KEY,
    crop_name TEXT,
    yield_amount DECIMAL,
    planting_date DATE
);

CREATE TABLE crop_variety (
    id INT PRIMARY KEY,
    variety_name TEXT,
    crop_type TEXT,
    seed_company TEXT
);

CREATE TABLE crop_category (
    id INT PRIMARY KEY,
    category_name TEXT
);
