tables.table_schema:
CREATE TABLE crop_rotation_schedule (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    rotation_date DATE
);

CREATE TABLE crop_rotation_schedule (
    crop_id UUID PRIMARY KEY,
    crop_name TEXT NOT NULL,
    planting_date TIMESTAMP NOT NULL,
    harvest_date TIMESTAMP NOT NULL
);

CREATE TABLE soil_health_data (
    soil_id UUID PRIMARY KEY,
    soil_type TEXT NOT NULL,
    pH_level DECIMAL NOT NULL,
    organic_matter DECIMAL NOT NULL
);

CREATE TABLE field_information (
    field_id UUID PRIMARY KEY,
    field_name TEXT NOT NULL,
    field_size DECIMAL NOT NULL
);

CREATE TABLE crop_rotation_schedule (
  id SERIAL PRIMARY KEY,
  crop_name VARCHAR(50) NOT NULL,
  rotation_sequence INTEGER NOT NULL,
  planting_date DATE NOT NULL
);

CREATE TABLE field_information (
  field_id SERIAL PRIMARY KEY,
  field_name VARCHAR(50) NOT NULL,
  field_size DECIMAL(10, 2) NOT NULL,
  soil_type VARCHAR(50) NOT NULL
);

CREATE TABLE rotation_crops (
  crop_id SERIAL PRIMARY KEY,
  crop_name VARCHAR(50) NOT NULL,
  crop_family VARCHAR(50) NOT NULL,
  planting_seeds INTEGER NOT NULL
);

CREATE TABLE crop_rotation_history (
  id SERIAL PRIMARY KEY,
  crop_id INTEGER,
  field_id INTEGER,
  rotation_date DATE NOT NULL
);
