tables.table_schema:
CREATE TABLE crop_yield_data (
    year INT,
    crop_name VARCHAR,
    yield_value FLOAT,
    location VARCHAR
);

CREATE TABLE crop_rotation (
    crop_name VARCHAR,
    previous_crop VARCHAR,
    rotation_cycle INT,
    suggested_crop VARCHAR
);

CREATE TABLE crop_yield_data (
    year INT,
    crop_name VARCHAR(255),
    yield_amount DECIMAL,
    location VARCHAR(255)
);

CREATE TABLE crop_rotation (
    rotation_id INT,
    crop_one VARCHAR(255),
    crop_two VARCHAR(255),
    crop_three VARCHAR(255)
);

CREATE TABLE crop_species (
    species_id INT,
    species_name VARCHAR(255),
    optimal_climate VARCHAR(255),
    soil_type VARCHAR(255)
);

CREATE TABLE historical_weather (
    date DATE,
    location VARCHAR(255),
    temperature DECIMAL,
    precipitation DECIMAL
);

CREATE TABLE crop_yield_data (
    crop_id INT,
    year INT,
    yield_kg_per_hectare FLOAT,
    PRIMARY KEY (crop_id, year)
);

CREATE TABLE crop (
    crop_id INT,
    crop_name VARCHAR(100),
    crop_type VARCHAR(50),
    PRIMARY KEY (crop_id)
);

CREATE TABLE soil_type (
    soil_id INT,
    soil_name VARCHAR(100),
    soil_texture VARCHAR(50),
    PRIMARY KEY (soil_id)
);
