tables.table_schema:
CREATE TABLE irrigation_schedule (
    schedule_id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    start_time TIME NOT NULL,
    end_time TIME NOT NULL,
    amount_used DECIMAL NOT NULL
);

CREATE TABLE irrigation_usage (
    usage_id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    area_id INT NOT NULL,
    amount_used DECIMAL NOT NULL
);

CREATE TABLE usage_patterns (
    usage_id INT PRIMARY KEY,
    date DATE,
    patterns TEXT
);

CREATE TABLE water_optimization (
    optimization_id INT PRIMARY KEY,
    area_id INT,
    optimization_strategy TEXT,
    optimization_date DATE
);

CREATE TABLE irrigation_schedules (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    time TIME NOT NULL,
    duration INTERVAL NOT NULL
);

CREATE TABLE water_usage (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    volume DECIMAL NOT NULL
);

CREATE TABLE optimization_data (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    efficiency DECIMAL NOT NULL
);

CREATE TABLE usage_patterns (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    pattern VARCHAR NOT NULL
);

CREATE TABLE irrigation_zones (
    zone_id INT PRIMARY KEY,
    zone_name VARCHAR(50),
    zone_location TEXT
);

CREATE TABLE weather_data (
    weather_date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE irrigation_logs (
    log_id INT PRIMARY KEY,
    schedule_id INT,
    date DATE,
    water_usage FLOAT
);
