tables.table_schema:
CREATE TABLE water_usage (
    id INT PRIMARY KEY,
    date DATE,
    volume DECIMAL
);

CREATE TABLE efficiency (
    id INT PRIMARY KEY,
    date DATE,
    efficiency_ratio DECIMAL,
    status VARCHAR
);

CREATE TABLE performance (
    id INT PRIMARY KEY,
    date DATE,
    water_loss DECIMAL,
    overall_score INT
);

CREATE TABLE IrrigationSystem (
    id INT PRIMARY KEY,
    location TEXT,
    sensor_id INT,
    water_usage FLOAT
);

CREATE TABLE EfficiencyReport (
    report_id INT PRIMARY KEY,
    sensor_id INT,
    efficiency_score FLOAT
);

CREATE TABLE PerformanceReport (
    report_id INT PRIMARY KEY,
    sensor_id INT,
    performance_rating TEXT
);

CREATE TABLE WaterConsumption (
    id INT PRIMARY KEY,
    date DATE,
    water_usage FLOAT
);

CREATE TABLE EfficiencyMetrics (
    id INT PRIMARY KEY,
    date DATE,
    efficiency_score FLOAT
);

CREATE TABLE PerformanceMetrics (
    id INT PRIMARY KEY,
    date DATE,
    performance_score FLOAT
);

CREATE TABLE IrrigationSystem (
    id INT PRIMARY KEY,
    system_name VARCHAR,
    location VARCHAR,
    installation_date DATE
);

CREATE TABLE MaintenanceLogs (
    id INT PRIMARY KEY,
    system_id INT,
    maintenance_date DATE,
    details TEXT
);
