CREATE TABLE WeatherForecast (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE HistoricalData (
    date DATE,
    avg_temperature FLOAT,
    avg_humidity FLOAT,
    total_precipitation FLOAT
);

CREATE TABLE IrrigationSchedule (
    date DATE,
    irrigation_duration FLOAT,
    irrigation_quantity FLOAT
);

CREATE TABLE AdjustmentLog (
    date DATE,
    adjustment_reason TEXT,
    adjustment_description TEXT
);

CREATE TABLE WeatherForecast (
    id INT PRIMARY KEY,
    forecast_date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE IrrigationSchedule (
    id INT PRIMARY KEY,
    date DATE,
    recommended_duration INT
);

CREATE TABLE Field (
    id INT PRIMARY KEY,
    field_name VARCHAR(50),
    crop_type VARCHAR(50),
    area FLOAT
);

CREATE TABLE IrrigationLog (
    id INT PRIMARY KEY,
    irrigation_date DATE,
    duration INT,
    field_id INT
);
