tables.table_schema:
CREATE TABLE irrigation_zones (
    id INT PRIMARY KEY,
    soil_type VARCHAR(50),
    slope DECIMAL(5,2),
    sun_exposure VARCHAR(50)
);

CREATE TABLE soil_information (
   soil_id SERIAL PRIMARY KEY,
   soil_type VARCHAR(255) NOT NULL,
   soil_texture VARCHAR(255) NOT NULL
);

CREATE TABLE soil_information (
    soil_id INT PRIMARY KEY,
    soil_type VARCHAR(50) NOT NULL,
    moisture_level FLOAT NOT NULL
);

CREATE TABLE slope_information (
    slope_id INT PRIMARY KEY,
    slope_degree FLOAT NOT NULL,
    slope_classification VARCHAR(50) NOT NULL
);

CREATE TABLE sun_exposure (
    sun_id INT PRIMARY KEY,
    sun_intensity FLOAT NOT NULL,
    sun_duration FLOAT NOT NULL
);
