tables.table_schema:
CREATE TABLE DroneAnalysis (
  analysis_id INT PRIMARY KEY,
  flight_id INT,
  analysis_result TEXT,
  CONSTRAINT fk_flight_id FOREIGN KEY (flight_id) REFERENCES DroneFlightData(flight_id)
);

CREATE TABLE DroneAltitude (
  drone_id INT,
  altitude FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE DroneSpeed (
  drone_id INT,
  speed FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE DroneGPS (
  drone_id INT,
  latitude FLOAT,
  longitude FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE drone_sensors (
  sensor_id SERIAL PRIMARY KEY,
  drone_id INT,
  sensor_type VARCHAR(50),
  sensor_value FLOAT
);
