tables.table_schema:
CREATE TABLE Drone_Equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(50),
    quantity INT,
    last_maintenance_date DATE
);

CREATE TABLE Spare_Parts (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(50),
    quantity INT,
    compatible_equipment VARCHAR(100)
);

CREATE TABLE equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    quantity INT,
    location VARCHAR(255)
);

CREATE TABLE spare_parts (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(255),
    quantity INT,
    location VARCHAR(255)
);

CREATE TABLE Drones (
    drone_id UUID PRIMARY KEY,
    drone_model TEXT NOT NULL,
    purchase_date DATE,
    location TEXT
);

CREATE TABLE Drones (
	drone_id INT PRIMARY KEY,
	drone_name VARCHAR(50),
	drone_model VARCHAR(50),
	drone_status VARCHAR(20)
);

CREATE TABLE Spare_Parts (
	part_id INT PRIMARY KEY,
	part_name VARCHAR(50),
	part_type VARCHAR(50),
	part_quantity INT
);
