tables.table_schema:
CREATE TABLE SurveyImages (
    image_id UUID PRIMARY KEY,
    survey_id UUID,
    image_data BYTEA,
    image_timestamp TIMESTAMP
);

CREATE TABLE CropTypes (
    crop_id UUID PRIMARY KEY,
    crop_name VARCHAR(50),
    growth_duration INTEGER,
    optimum_temperature_range VARCHAR(20)
);

CREATE TABLE `survey_area` (
  `id` SERIAL PRIMARY KEY,
  `name` VARCHAR(50) NOT NULL,
  `area_size` DECIMAL NOT NULL,
  `location` GEOGRAPHY NOT NULL
);

CREATE TABLE `map_tile` (
  `id` SERIAL PRIMARY KEY,
  `tile_name` VARCHAR(50) NOT NULL,
  `image_url` TEXT NOT NULL,
  `map_area` GEOGRAPHY NOT NULL
);

CREATE TABLE `crop_data` (
  `id` SERIAL PRIMARY KEY,
  `crop_name` VARCHAR(50) NOT NULL,
  `yield_per_area` DECIMAL NOT NULL,
  `harvest_timestamp` TIMESTAMP NOT NULL
);
