tables.table_schema:
CREATE TABLE pesticide_recommendations (
    pesticide_id INT PRIMARY KEY,
    pesticide_name VARCHAR(255),
    crop_type VARCHAR(100),
    recommended_usage VARCHAR(100)
);

CREATE TABLE herbicide_recommendations (
    herbicide_id INT PRIMARY KEY,
    herbicide_name VARCHAR(255),
    crop_type VARCHAR(100),
    recommended_usage VARCHAR(100)
);

CREATE TABLE drone_data (
    drone_id INT PRIMARY KEY,
    location VARCHAR(100),
    recommendation_id INT,
    FOREIGN KEY (recommendation_id) REFERENCES pesticide_recommendations(pesticide_id)
);
