tables.table_schema:
CREATE TABLE texture_measurements (
  measurement_id UUID PRIMARY KEY,
  texture TEXT,
  measurement_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE structure_measurements (
  measurement_id UUID PRIMARY KEY,
  structure TEXT,
  measurement_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE soil_measurements (
  id INT PRIMARY KEY,
  measurement_date TIMESTAMP,
  texture VARCHAR(50),
  structure VARCHAR(50)
);

CREATE TABLE measurement_notes (
  id INT PRIMARY KEY,
  measurement_id INT,
  note TEXT,
  FOREIGN KEY (measurement_id) REFERENCES soil_measurements(id)
);

CREATE TABLE SoilTexture ( 
  id INT PRIMARY KEY, 
  timestamp TIMESTAMP, 
  texture VARCHAR(50), 
  moisture_level FLOAT 
);

CREATE TABLE SoilStructure ( 
  id INT PRIMARY KEY, 
  timestamp TIMESTAMP, 
  structure_type VARCHAR(50), 
  compaction_level FLOAT 
);

CREATE TABLE SoilPH ( 
  id INT PRIMARY KEY, 
  timestamp TIMESTAMP, 
  ph_level FLOAT 
);

CREATE TABLE MoistureContent ( 
  id INT PRIMARY KEY, 
  timestamp TIMESTAMP, 
  moisture_reading FLOAT 
);

CREATE TABLE SoilTexture ( 
   measurement_id SERIAL PRIMARY KEY, 
   timestamp TIMESTAMP NOT NULL, 
   texture VARCHAR(50) NOT NULL 
 );

CREATE TABLE SoilStructure ( 
   measurement_id SERIAL PRIMARY KEY, 
   timestamp TIMESTAMP NOT NULL, 
   structure VARCHAR(50) NOT NULL 
 );

CREATE TABLE SoilpH ( 
   measurement_id SERIAL PRIMARY KEY, 
   timestamp TIMESTAMP NOT NULL, 
   ph_level FLOAT NOT NULL 
 );

CREATE TABLE SoilMoisture ( 
   measurement_id SERIAL PRIMARY KEY, 
   timestamp TIMESTAMP NOT NULL, 
   moisture_level FLOAT NOT NULL 
 );

CREATE TABLE SoilComposition ( 
   measurement_id SERIAL PRIMARY KEY, 
   timestamp TIMESTAMP NOT NULL, 
   component VARCHAR(50) NOT NULL, 
   percentage FLOAT NOT NULL 
 );
