tables.table_schema:
CREATE TABLE soil_nutrient_reports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  nutrient_levels JSONB,
  recommended_fertilizers VARCHAR[]
);

CREATE TABLE fertilizer_recommendations (
    recommendation_id UUID PRIMARY KEY,
    nutrient_type VARCHAR(50),
    recommended_fertilizer VARCHAR(100),
    application_rate FLOAT
);

CREATE TABLE fertilizer_recommendations (
    recommendation_id INT PRIMARY KEY,
    crop_type VARCHAR(255),
    recommended_fertilizer VARCHAR(255),
    application_rate FLOAT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE historical_data (
    id INT PRIMARY KEY,
    soil_type VARCHAR(50),
    nutrient_levels JSONB,
    date_recorded DATE
);

CREATE TABLE recommendations (
    id INT PRIMARY KEY,
    deficiency_id INT,
    fertilizer_id INT,
    date_recommended DATE
);
