tables.table_schema:
CREATE TABLE SoilCompositionData (
    soil_id INT PRIMARY KEY,
    location VARCHAR(50),
    pH_level DECIMAL,
    nutrient_levels JSONB
);

CREATE TABLE SoilHealthAnalysis (
    analysis_id INT PRIMARY KEY,
    soil_id INT,
    analysis_date DATE,
    health_rating DECIMAL
);

CREATE TABLE SoilHealth (
	id INT PRIMARY KEY,
	sample_id INT,
	health_rating INT,
	comments TEXT
);

CREATE TABLE soil_health (
    health_id SERIAL PRIMARY KEY,
    sample_id INT REFERENCES soil_samples(sample_id),
    compaction VARCHAR(50),
    organic_matter DECIMAL
);
