tables.table_schema:
CREATE TABLE soil_parameters ( 
    parameter_id UUID PRIMARY KEY, 
    parameter_name STRING, 
    parameter_value FLOAT 
);

CREATE TABLE soil_samples ( 
    sample_id UUID PRIMARY KEY, 
    sample_date TIMESTAMP, 
    parameter_id UUID REFERENCES soil_parameters(parameter_id), 
    moisture_level FLOAT, 
    nutrient_level FLOAT, 
    pH_level FLOAT 
);

CREATE TABLE SoilHealthIndex (
    id INT PRIMARY KEY,
    parameter1 FLOAT,
    parameter2 FLOAT,
    parameter3 FLOAT
);

CREATE TABLE SoilMoisture (
    id INT PRIMARY KEY,
    soil_id INT,
    moisture_level FLOAT,
    measurement_date DATE
);

CREATE TABLE SoilNutrients (
    id INT PRIMARY KEY,
    soil_id INT,
    nutrient_type VARCHAR(255),
    nutrient_level FLOAT
);

CREATE TABLE monitoring_log (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(100) NOT NULL
);

CREATE TABLE TemperatureRecords (
  record_id SERIAL PRIMARY KEY,
  date_recorded DATE,
  temperature FLOAT
);

CREATE TABLE SoilNutrients (
  nutrient_id SERIAL PRIMARY KEY,
  nutrient_name VARCHAR(50),
  nutrient_description TEXT
);

CREATE TABLE SoilMoisture (
  moisture_id SERIAL PRIMARY KEY,
  sample_id INT,
  moisture_content FLOAT,
  FOREIGN KEY (sample_id) REFERENCES SoilSamples(sample_id)
);

CREATE TABLE SoilQualityIndex (
  soil_quality_index_id SERIAL PRIMARY KEY,
  sample_id INT,
  overall_health_index FLOAT,
  FOREIGN KEY (sample_id) REFERENCES SoilSamples(sample_id)
);
