tables.table_schema:
CREATE TABLE FieldTechnician (
  technician_id INT PRIMARY KEY,
  technician_name VARCHAR(50),
  technician_email VARCHAR(50),
  technician_phone VARCHAR(15)
);

CREATE TABLE field_technicians (
    technician_id INT PRIMARY KEY,
    technician_name VARCHAR(50),
    technician_email VARCHAR(100),
    technician_phone VARCHAR(15)
);

CREATE TABLE Technician (
    technician_id INT PRIMARY KEY,
    technician_name VARCHAR(50),
    technician_contact VARCHAR(15),
    technician_email VARCHAR(50)
);

CREATE TABLE FieldTechnicianAssignment (
    assignment_id INT PRIMARY KEY,
    schedule_id INT,
    technician_id INT,
    assigned_date DATE
);

CREATE TABLE SoilHealthTask (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(50),
    task_description TEXT
);

CREATE TABLE field_technicians (
    technician_id UUID PRIMARY KEY,
    technician_name VARCHAR,
    technician_phone VARCHAR,
    technician_email VARCHAR
);

CREATE TABLE tasks (
    task_id UUID PRIMARY KEY,
    task_name VARCHAR,
    task_description TEXT,
    task_duration INT
);

CREATE TABLE field_sites (
    site_id UUID PRIMARY KEY,
    site_name VARCHAR,
    site_location VARCHAR
);
