tables.table_schema:
CREATE TABLE CropYieldData (
    region_name VARCHAR(50),
    crop_type VARCHAR(50),
    yield_amount INT
);

CREATE TABLE crop_yield_data (
    crop_id INT PRIMARY KEY,
    crop_name STRING,
    yield_amount INT,
    region STRING
);

CREATE TABLE region_data (
    region_id INT PRIMARY KEY,
    region_name STRING,
    avg_yield_amount FLOAT,
    total_crops INT
);

CREATE TABLE crop_yield_data (
  region_id INT,
  crop_type VARCHAR,
  yield_kg INT
);

CREATE TABLE region_info (
  region_id INT,
  region_name VARCHAR,
  location VARCHAR
);

CREATE TABLE trend_analysis (
  trend_id INT,
  region_id INT,
  trend_type VARCHAR
);

CREATE TABLE region (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50)
);

CREATE TABLE crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    crop_type VARCHAR(50),
    crop_season VARCHAR(50)
);
