tables.table_schema:
CREATE TABLE Weather_Data (
  date DATE,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE Crop_Diseases (
  disease_name VARCHAR(255),
  cause TEXT,
  prevention TEXT
);

CREATE TABLE weather_data (
  id INT PRIMARY KEY,
  temperature FLOAT,
  humidity FLOAT,
  pressure FLOAT
);

CREATE TABLE crop_data (
  id INT PRIMARY KEY,
  crop_name VARCHAR(255),
  crop_type VARCHAR(255),
  planting_date DATE
);

CREATE TABLE disease_data (
  id INT PRIMARY KEY,
  disease_name VARCHAR(255),
  affected_crop VARCHAR(255),
  severity FLOAT
);

CREATE TABLE weather_data (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE crop_info (
    crop_name VARCHAR,
    disease_susceptibility FLOAT,
    recommended_actions VARCHAR
);

CREATE TABLE disease_records (
    date DATE,
    crop_name VARCHAR,
    disease_name VARCHAR,
    detected BOOLEAN
);

CREATE TABLE weather_forecast (
    date DATE,
    forecasted_temperature FLOAT,
    forecasted_humidity FLOAT,
    forecasted_precipitation FLOAT
);
