tables.table_schema:
CREATE TABLE market_prices_of_agricultural_products (
    product_id INT PRIMARY KEY,
    product_name STRING,
    price DECIMAL,
    quantity INT
);

CREATE TABLE market_prices (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    price DECIMAL(10, 2),
    date TIMESTAMP
);

CREATE TABLE agricultural_products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    product_category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name STRING,
    category STRING,
    price DECIMAL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name STRING,
    email STRING,
    phone_number STRING
);
