tables.table_schema:
CREATE TABLE CropRotation (
 id INT PRIMARY KEY,
 crop_name VARCHAR(255),
 rotation_order INT
);

CREATE TABLE Field (
 id INT PRIMARY KEY,
 field_name VARCHAR(255),
 size FLOAT
);

CREATE TABLE RotationHistory (
 id INT PRIMARY KEY,
 field_id INT,
 crop_id INT,
 rotation_date DATE
);
