tables.table_schema:
CREATE TABLE Water_Usage_Data (
    id INT PRIMARY KEY,
    date DATE,
    time TIME,
    water_consumed DECIMAL
);

CREATE TABLE WaterUsageData (
    id INT PRIMARY KEY,
    date TIMESTAMP,
    location VARCHAR,
    water_consumed DECIMAL
);

CREATE TABLE IrrigationSystems (
    system_id INT PRIMARY KEY,
    location VARCHAR,
    water_source VARCHAR
);

CREATE TABLE WeatherData (
    date TIMESTAMP,
    temperature DECIMAL,
    humidity DECIMAL
);

CREATE TABLE water_sources (
  id UUID PRIMARY KEY,
  source_name STRING,
  location STRING,
  water_flow_rate FLOAT
);

CREATE TABLE irrigation_zones (
  id UUID PRIMARY KEY,
  zone_name STRING,
  zone_location STRING,
  soil_type STRING
);

CREATE TABLE irrigation_schedules (
  id UUID PRIMARY KEY,
  schedule_date DATE,
  zone_id UUID,
  water_amount FLOAT
);

CREATE TABLE water_usage_logs (
  id UUID PRIMARY KEY,
  log_date DATE,
  zone_id UUID,
  water_used FLOAT
);

CREATE TABLE soil_moisture_levels (
  id UUID PRIMARY KEY,
  zone_id UUID,
  moisture_level FLOAT,
  recorded_at TIMESTAMP
);
