tables.table_schema:
CREATE TABLE remote_sensing_images (
    id INT PRIMARY KEY,
    image_url VARCHAR(255),
    capture_date DATE,
    resolution VARCHAR(50)
);

CREATE TABLE remote_sensing_datasets (
    dataset_id INT PRIMARY KEY,
    dataset_name VARCHAR(100),
    description TEXT,
    created_on TIMESTAMP
);

CREATE TABLE images (
    id INT PRIMARY KEY,
    image_name VARCHAR(255) NOT NULL,
    image_type VARCHAR(50) NOT NULL,
    capture_date DATE NOT NULL
);

CREATE TABLE image_datasets (
    image_id INT,
    dataset_id INT,
    FOREIGN KEY (image_id) REFERENCES images(id),
    FOREIGN KEY (dataset_id) REFERENCES datasets(id)
);

CREATE TABLE image_tags (
    tag_id INT PRIMARY KEY,
    image_id INT,
    tag_name VARCHAR(50),
    FOREIGN KEY (image_id) REFERENCES remote_sensing_images(id)
);

CREATE TABLE dataset_tags (
    tag_id INT PRIMARY KEY,
    dataset_id INT,
    tag_name VARCHAR(50),
    FOREIGN KEY (dataset_id) REFERENCES remote_sensing_datasets(dataset_id)
);

CREATE TABLE image_tags (
  id INT PRIMARY KEY,
  image_id INT,
  tag_name VARCHAR(50),
  FOREIGN KEY (image_id) REFERENCES images(id)
);
