tables.table_schema:
CREATE TABLE geospatial_changes (
    id SERIAL PRIMARY KEY,
    coordinates GEOMETRY,
    change_description TEXT,
    change_timestamp TIMESTAMP
);

CREATE TABLE user_tracking (
    user_id SERIAL PRIMARY KEY,
    location GEOMETRY,
    last_updated TIMESTAMP
);

CREATE TABLE geospatial_data_changes (
    change_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP,
    location GEOMETRY
);

CREATE TABLE geospatial_data_updates (
    update_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP,
    updated_location GEOMETRY,
    previous_location GEOMETRY
);

CREATE TABLE geospatial_data_history (
    entry_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP,
    location_history JSONB
);

CREATE TABLE changes_over_time (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  geospatial_data GEOMETRY
);

CREATE TABLE change_types (
  change_id INT,
  change_type VARCHAR(50),
  description TEXT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  location GEOMETRY
);

CREATE TABLE user_activity (
  activity_id INT,
  user_id INT,
  activity_type VARCHAR(50),
  activity_timestamp TIMESTAMP
);

CREATE TABLE objects (object_id SERIAL PRIMARY KEY,
  name VARCHAR,
  description TEXT);

CREATE TABLE users (user_id SERIAL PRIMARY KEY,
  username VARCHAR,
  email VARCHAR);

CREATE TABLE spatial_data (object_id INT PRIMARY KEY,
  geometry GEOGRAPHY,
  creation_timestamp TIMESTAMP);
