tables.table_schema:
CREATE TABLE gis_data (
    id INT PRIMARY KEY,
    location GEOMETRY
);

CREATE TABLE spatial_data (
    id UUID PRIMARY KEY,
    location GEOMETRY(POINT),
    data JSONB
);

CREATE TABLE visualization_settings (
    id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(id),
    theme TEXT,
    chart_type TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username STRING,
    email STRING,
    location STRING
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name STRING,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE users (user_id INT,
  username VARCHAR(255),
  email VARCHAR(255));

CREATE TABLE layers (layer_id INT,
  layer_name VARCHAR(255),
  visibility BOOLEAN);

CREATE TABLE maps (map_id INT,
  map_name VARCHAR(255),
  creation_date DATE);
