tables.table_schema:
CREATE TABLE Satellite_Images (
    image_id INT PRIMARY KEY,
    image_date DATE,
    image_url VARCHAR,
    satellite_name VARCHAR
);

CREATE TABLE SatelliteImages (
    id INT PRIMARY KEY,
    image_url TEXT,
    acquisition_date DATE,
    satellite_name TEXT
);

CREATE TABLE Satellites (
    satellite_id INT PRIMARY KEY,
    satellite_name TEXT,
    launch_date DATE,
    orbit_type TEXT
);

CREATE TABLE ImageTags (
    tag_id INT PRIMARY KEY,
    image_id INT,
    tag_name TEXT,
    FOREIGN KEY (image_id) REFERENCES SatelliteImages(id)
);

CREATE TABLE image_tags (
    tag_id INT PRIMARY KEY,
    image_id INT REFERENCES satellite_images(image_id),
    tag_name VARCHAR(50)
);
