CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    temperature FLOAT,
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE sensor_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_type VARCHAR(50),
    installation_location VARCHAR(255)
);

