tables.table_schema:
CREATE TABLE SatelliteImages (
  id INT PRIMARY KEY,
  image_name TEXT,
  image_date DATE,
  image_size INT
);

CREATE TABLE ImageTags (
  tag_id INT PRIMARY KEY,
  image_id INT,
  tag_name TEXT,
  FOREIGN KEY (image_id) REFERENCES SatelliteImages(id)
);

CREATE TABLE satellite_images (
  image_id UUID PRIMARY KEY,
  image_name TEXT,
  image_source TEXT,
  image_date DATE
);

CREATE TABLE image_tags (
  tag_id UUID PRIMARY KEY,
  tag_name TEXT
);

CREATE TABLE image_categories (
  category_id UUID PRIMARY KEY,
  category_name TEXT
);

CREATE TABLE image_access_info (
  access_id UUID PRIMARY KEY,
  metadata_id UUID,
  access_date TIMESTAMP,
  FOREIGN KEY (metadata_id) REFERENCES image_metadata (metadata_id)
);
