tables.table_schema:
CREATE TABLE land_cover_data (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  location VARCHAR,
  land_type VARCHAR
);

CREATE TABLE LandCoverData (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    land_cover_type VARCHAR(50),
    land_cover_percentage FLOAT
);

CREATE TABLE LandCoverChanges (
    change_id INT PRIMARY KEY,
    location VARCHAR(255),
    change_type VARCHAR(50),
    change_date DATE
);

CREATE TABLE RemoteSensingData (
    data_id INT PRIMARY KEY,
    location VARCHAR(255),
    sensor_type VARCHAR(50),
    captured_date TIMESTAMP
);

CREATE TABLE land_cover_categories (
   category_id SERIAL PRIMARY KEY,
   category_name TEXT
);

CREATE TABLE temporal_change (
   change_id SERIAL PRIMARY KEY,
   land_cover_id INT,
   change_date DATE,
   change_percentage FLOAT
);

CREATE TABLE spatial_data (
   spatial_id SERIAL PRIMARY KEY,
   location_name TEXT,
   latitude DECIMAL,
   longitude DECIMAL
);

CREATE TABLE land_cover_analysis (
    image_id  UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    location GEOMETRY,
    land_cover_type TEXT
);

CREATE TABLE land_cover_categories (
    id  SERIAL PRIMARY KEY,
    category_name TEXT
);

CREATE TABLE satellite_images (
    image_id  UUID PRIMARY KEY,
    image_path TEXT,
    acquisition_date DATE,
    resolution FLOAT
);

CREATE TABLE analysis_results (
    result_id SERIAL PRIMARY KEY,
    image_id UUID REFERENCES satellite_images(image_id),
    analysis_date DATE,
    change_percentage FLOAT
);

CREATE TABLE analysis_logs (
    log_id SERIAL PRIMARY KEY,
    analysis_date DATE,
    entries TEXT
);
