tables.table_schema:
CREATE TABLE SpatialData1 (
  id INT PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE SpatialData2 (
  id INT PRIMARY KEY,
  city VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE spatial_data (
    id SERIAL PRIMARY KEY,
    location geography(POINT),
    data_source text
);

CREATE TABLE analysis_results (
    id SERIAL PRIMARY KEY,
    spatial_id INT,
    result_value FLOAT,
    FOREIGN KEY (spatial_id) REFERENCES spatial_data(id)
);

CREATE TABLE data_sources (
    id SERIAL PRIMARY KEY,
    name TEXT UNIQUE
);

CREATE TABLE spatial_data_1 (
  id INT PRIMARY KEY,
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE spatial_data_2 (
  id INT PRIMARY KEY,
  location_name VARCHAR(255),
  region_name VARCHAR(255)
);

CREATE TABLE spatial_data_3 (
  id INT PRIMARY KEY,
  measurement_value DECIMAL,
  measurement_unit VARCHAR(50)
);

CREATE TABLE spatial_analysis_results (
  id INT PRIMARY KEY,
  analysis_date DATE,
  result_description TEXT
);

CREATE TABLE spatial_data_source1 (
    id INT,
    geometry GEOGRAPHY,
    attribute1 TEXT,
    attribute2 FLOAT
);

CREATE TABLE spatial_data_source2 (
    id INT,
    geometry GEOMETRY,
    attribute3 TEXT,
    attribute4 FLOAT
);

CREATE TABLE analysis_results (
    id INT,
    result TEXT,
    score FLOAT,
    analysis_date DATE
);

CREATE TABLE analysis_summary (
    id INT,
    result_id INT,
    summary TEXT
);
