CREATE TABLE historical_weather_data (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE location_data (
    location_id INT,
    location_name VARCHAR,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE weather_data (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    temperature FLOAT,
    precipitation FLOAT
);

CREATE TABLE crop_data (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    plant_date DATE,
    harvest_date DATE
);

CREATE TABLE location_data (
    id SERIAL PRIMARY KEY,
    location_name VARCHAR(50) NOT NULL,
    city VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE analysis_data (
    id SERIAL PRIMARY KEY,
    analysis_date DATE,
    analysis_result FLOAT,
    notes TEXT
);
