tables.table_schema:
CREATE TABLE soil_moisture_levels (
    field_id INT,
    date DATE,
    moisture_level DECIMAL
);

CREATE TABLE field (
  field_id INT PRIMARY KEY,
  field_name VARCHAR(255) NOT NULL,
  soil_moisture_level DECIMAL(5, 2)
);

CREATE TABLE fields (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE moisture_data (
    data_id INT PRIMARY KEY,
    field_id INT NOT NULL,
    moisture_level DECIMAL NOT NULL,
    recorded_at TIMESTAMP NOT NULL
);

CREATE TABLE weather_conditions (
    condition_id INT PRIMARY KEY,
    description VARCHAR(100) NOT NULL
);

CREATE TABLE field_locations (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(50) NOT NULL
);

CREATE TABLE soil_moisture_measurements (
    measurement_id INT PRIMARY KEY,
    field_id INT NOT NULL,
    moisture_level DECIMAL NOT NULL,
    measurement_date DATE NOT NULL
);

CREATE TABLE field_properties (
    field_id INT PRIMARY KEY,
    field_area DECIMAL NOT NULL,
    soil_type VARCHAR(50) NOT NULL
);

CREATE TABLE field_crops (
    crop_id INT PRIMARY KEY,
    field_id INT NOT NULL,
    crop_name VARCHAR(50) NOT NULL,
    planting_date DATE NOT NULL
);
