tables.table_schema:
CREATE TABLE crop_yields (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    yield_amount INT NOT NULL,
    yield_date DATE NOT NULL
);

CREATE TABLE sales_data (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    sale_amount INT NOT NULL,
    sale_date DATE NOT NULL
);

CREATE TABLE crop_yield_data (
    crop_id INT NOT NULL,
    yield_kg DECIMAL NOT NULL,
    sale_price DECIMAL NOT NULL,
    sale_date DATE NOT NULL
);

CREATE TABLE crop_sales (
    sale_id INT NOT NULL,
    crop_id INT NOT NULL,
    sold_quantity INT NOT NULL,
    total_amount DECIMAL NOT NULL,
    sale_date DATE NOT NULL
);

CREATE TABLE financial_analysis (
    analysis_id INT NOT NULL,
    total_revenue DECIMAL NOT NULL,
    total_cost DECIMAL NOT NULL,
    profit DECIMAL NOT NULL,
    analysis_date DATE NOT NULL
);

CREATE TABLE YieldData (
  crop_name VARCHAR(255),
  yield_amount INT,
  date_recorded DATE
);

CREATE TABLE SalesData (
  crop_name VARCHAR(255),
  sale_amount INT,
  sale_date DATE
);

CREATE TABLE RevenueData (
  crop_name VARCHAR(255),
  revenue DECIMAL(12, 2),
  month_year VARCHAR(10)
);

CREATE TABLE crop_yield_data (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    yield_amount DECIMAL NOT NULL,
    yield_date DATE NOT NULL
);

CREATE TABLE sales_data (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    quantity_sold INT NOT NULL,
    sale_amount DECIMAL NOT NULL,
    sale_date DATE NOT NULL
);

CREATE TABLE financial_analysis (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    total_yield_amount DECIMAL NOT NULL,
    total_sale_amount DECIMAL NOT NULL,
    profit_amount DECIMAL NOT NULL
);

CREATE TABLE crop_varieties (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    variety_name VARCHAR(50) NOT NULL,
    planting_date DATE NOT NULL
);

CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    temperature DECIMAL NOT NULL,
    humidity DECIMAL NOT NULL,
    weather_date DATE NOT NULL
);
