tables.table_schema:
CREATE TABLE Pests (
	p_id UUID PRIMARY KEY,
	p_name STRING,
	p_type STRING,
	infestation_level INT
);

CREATE TABLE PestInfestations (
    pest_id INT PRIMARY KEY,
    pest_name VARCHAR(50) NOT NULL,
    infestation_date TIMESTAMP NOT NULL
);

CREATE TABLE InfestationImpact (
    impact_id INT PRIMARY KEY,
    pest_id INT NOT NULL,
    crop_id INT NOT NULL,
    impact_description TEXT NOT NULL
);

CREATE TABLE Farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50) NOT NULL,
    contact_number VARCHAR(15) NOT NULL
);

CREATE TABLE PestControl (
    id SERIAL PRIMARY KEY,
    control_method VARCHAR(50) NOT NULL,
    use_date DATE,
    infestation_id INT
);
