tables.table_schema:
CREATE TABLE irrigation_data (
    farm_id INT,
    date DATE,
    water_usage DECIMAL,
    temperature DECIMAL
);

CREATE TABLE field_info (
    field_id INT,
    field_name VARCHAR(50),
    location VARCHAR(50),
    crop_type VARCHAR(50)
);

CREATE TABLE irrigation_zone (
    zone_id UUID PRIMARY KEY,
    zone_name VARCHAR(100) NOT NULL,
    crop_type VARCHAR(50) NOT NULL,
    area_sqm FLOAT NOT NULL
);

CREATE TABLE irrigation_schedule (
    schedule_id UUID PRIMARY KEY,
    zone_id UUID REFERENCES irrigation_zone(zone_id),
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL,
    frequency VARCHAR(20) NOT NULL
);

CREATE TABLE soil_moisture (
    reading_id UUID PRIMARY KEY,
    zone_id UUID REFERENCES irrigation_zone(zone_id),
    reading_time TIMESTAMP NOT NULL,
    moisture_level FLOAT NOT NULL
);

CREATE TABLE water_flow (
    flow_id UUID PRIMARY KEY,
    zone_id UUID REFERENCES irrigation_zone(zone_id),
    flow_rate FLOAT NOT NULL,
    flow_time TIMESTAMP NOT NULL
);

CREATE TABLE irrigation_zones (
    zone_id INT PRIMARY KEY,
    zone_name TEXT,
    irrigation_area FLOAT,
    soil_type TEXT
);

CREATE TABLE water_usage (
    usage_id INT PRIMARY KEY,
    zone_id INT,
    date DATE,
    amount_used FLOAT
);

CREATE TABLE weather_data (
    weather_id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE soil_moisture (
    moisture_id INT PRIMARY KEY,
    zone_id INT,
    date DATE,
    moisture_level FLOAT
);
