tables.table_schema:
CREATE TABLE EquipmentMaintenanceRecords ( 
    equipment_id INT PRIMARY KEY, 
    equipment_name VARCHAR(50), 
    maintenance_date DATE, 
    maintenance_description TEXT 
);

CREATE TABLE Equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(255) NOT NULL,
  last_maintenance_date DATE,
  purchase_date DATE
);

CREATE TABLE Technicians (
  technician_id INT PRIMARY KEY,
  technician_name VARCHAR(255) NOT NULL,
  technician_email VARCHAR(255),
  technician_phone VARCHAR(20)
);

CREATE TABLE technicians (
  technician_id SERIAL PRIMARY KEY,
  technician_name VARCHAR(50) NOT NULL,
  phone_number VARCHAR(15),
  email_address VARCHAR(50)
);

CREATE TABLE Equipment (
	equipment_id INT PRIMARY KEY,
	equipment_name VARCHAR(50),
	equipment_type VARCHAR(50),
	maintenance_date DATE
);

CREATE TABLE Technicians (
	technician_id INT PRIMARY KEY,
	technician_name VARCHAR(50),
	technician_specialty VARCHAR(50)
);

CREATE TABLE MaintenanceChecklist (
	checklist_id INT PRIMARY KEY,
	maintenance_type VARCHAR(50),
	checklist_item TEXT
);
