tables.table_schema:
CREATE TABLE CropFields (
    field_id UUID PRIMARY KEY,
    field_name VARCHAR(255),
    field_area FLOAT,
    coordinates GEOMETRY
);

CREATE TABLE FieldOwners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(255),
    contact_info JSON,
    field_id UUID REFERENCES CropFields(field_id)
);

CREATE TABLE CropFieldMapping (
    field_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE FieldStatus (
    status_id INT PRIMARY KEY,
    field_id INT,
    status VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE CropTypes (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    growth_period VARCHAR(50)
);

CREATE TABLE CropFields (
    id INT PRIMARY KEY,
    field_name VARCHAR(255) NOT NULL,
    latitude DOUBLE PRECISION NOT NULL,
    longitude DOUBLE PRECISION NOT NULL
);

CREATE TABLE Crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    crop_type VARCHAR(100) NOT NULL
);

CREATE TABLE crop_fields (
  field_id INT PRIMARY KEY,
  field_name TEXT,
  field_location GEOGRAPHY
);
