tables.table_schema:
CREATE TABLE MarketTrends (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    market_price DECIMAL,
    planting_date DATE
);

CREATE TABLE PlantingDecisions (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255) REFERENCES MarketTrends(crop_name),
    planting_area DECIMAL,
    planting_date DATE
);

CREATE TABLE market_trends (
    product_id INT PRIMARY KEY,
    product_name STRING,
    market_price DECIMAL
);

CREATE TABLE planting_decisions (
    decision_id INT PRIMARY KEY,
    decision_date DATE,
    plant_type STRING,
    planting_location STRING
);

CREATE TABLE market_analysis (
    analysis_id INT PRIMARY KEY,
    analysis_date DATE,
    trend_description STRING,
    recommendations STRING
);

CREATE TABLE crop_data (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    market_price DECIMAL(10, 2),
    planting_season VARCHAR(20)
);

CREATE TABLE market_demand (
    demand_id INT PRIMARY KEY,
    crop_id INT,
    demand_value INT,
    demand_date DATE
);

CREATE TABLE planting_decisions (
    decision_id INT PRIMARY KEY,
    crop_id INT,
    planting_date DATE,
    planting_location VARCHAR(50)
);

CREATE TABLE market_analysis (
    analysis_id INT PRIMARY KEY,
    crop_id INT,
    market_growth_rate DECIMAL(5, 2),
    analysis_date DATE
);

CREATE TABLE MarketSize (
   market_id INT PRIMARY KEY,
   market_name VARCHAR(50) NOT NULL,
   market_size DECIMAL(10,2)
);

CREATE TABLE PlantingDecisions (
   decision_id INT PRIMARY KEY,
   crop_name VARCHAR(50) NOT NULL,
   planting_date DATE,
   harvest_date DATE
);

CREATE TABLE MarketTrends (
   trend_id INT PRIMARY KEY,
   market_id INT,
   trend_description TEXT,
   trend_date DATE
);

CREATE TABLE PlantingSchedule (
   schedule_id INT PRIMARY KEY,
   crop_name VARCHAR(50) NOT NULL,
   planting_start_date DATE,
   planting_end_date DATE
);

CREATE TABLE CropVarieties (
   variety_id INT PRIMARY KEY,
   crop_name VARCHAR(50) NOT NULL,
   variety_name VARCHAR(50) NOT NULL,
   planting_season VARCHAR(20)
);
