tables.table_schema:
CREATE TABLE crop_yields (
 id UUID PRIMARY KEY,
 crop_type TEXT,
 yield DECIMAL,
 year INT
);

CREATE TABLE fertilizer_applications (
  id INT PRIMARY KEY,
  application_date DATE,
  type VARCHAR(255),
  quantity INT
);

CREATE TABLE crop_yields (
  id INT PRIMARY KEY,
  batch_id INT,
  yield_amount DECIMAL(10,2),
  yield_date DATE
);

CREATE TABLE effectiveness_analysis (
  id INT PRIMARY KEY,
  batch_id INT,
  analysis_date DATE,
  effectiveness_score DECIMAL(5,2)
);

CREATE TABLE fields (
  id INT PRIMARY KEY,
  field_name VARCHAR(50),
  field_size DECIMAL(10,2),
  location VARCHAR(50)
);

CREATE TABLE farmer (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50),
    location VARCHAR(50),
    contact_number VARCHAR(20)
);

CREATE TABLE field (
    field_id INT PRIMARY KEY,
    location VARCHAR(50),
    size DECIMAL,
    crop_id INT
);
