tables.table_schema:
CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE SoilMoisture (
    id INT PRIMARY KEY,
    sensor_id INT,
    moisture_level FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE IrrigationSchedule (
    id INT PRIMARY KEY,
    sensor_id INT,
    start_time TIMESTAMP,
    duration INTERVAL
);
