tables.table_schema:
CREATE TABLE forecast_predictions (
    prediction_id INT PRIMARY KEY,
    prediction_date DATE,
    weather_condition VARCHAR(50),
    temperature FLOAT
);

CREATE TABLE forecasting (
  id INT PRIMARY KEY,
  prediction VARCHAR,
  temperature FLOAT
);

CREATE TABLE weather_data (
  weather_id INT PRIMARY KEY,
  location VARCHAR,
  timestamp TIMESTAMP,
  temperature FLOAT
);

CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    date DATE,
    temperature INT,
    humidity INT
);

CREATE TABLE forecast_data (
    id INT PRIMARY KEY,
    date DATE,
    forecast VARCHAR,
    confidence_level FLOAT
);

CREATE TABLE prediction_updates (
    id INT PRIMARY KEY,
    date DATE,
    updated_forecast VARCHAR,
    update_time TIMESTAMP
);

CREATE TABLE WeatherForecast (
    forecast_id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    weather_conditions TEXT
);

CREATE TABLE ForecastLogs (
    log_id INT PRIMARY KEY,
    forecast_id INT,
    timestamp TIMESTAMP,
    notes TEXT
);

CREATE TABLE UpdateHistory (
    update_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    updated_table TEXT,
    action_performed TEXT
);

CREATE TABLE weather_data_logs ( 
  log_id INT, 
  timestamp TIMESTAMP, 
  message VARCHAR(255) 
);
