tables.table_schema:
CREATE TABLE atmospheric_pressure (
  timestamp TIMESTAMP,
  pressure DECIMAL,
  location VARCHAR(255)
);

CREATE TABLE atmospheric_pressure_data (
    timestamp TIMESTAMP,
    pressure FLOAT
);

CREATE TABLE pressure_patterns_detected (
    pattern_id SERIAL PRIMARY KEY,
    pattern VARCHAR,
    start_timestamp TIMESTAMP,
    end_timestamp TIMESTAMP
);

CREATE TABLE atmospheric_data (
    id INT PRIMARY KEY,
    time TIMESTAMP,
    pressure FLOAT
);

CREATE TABLE pressure_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR,
    detection_method VARCHAR
);

CREATE TABLE pattern_data (
    data_id INT PRIMARY KEY,
    pattern_id INT,
    time TIMESTAMP,
    severity_level INT
);
